import { useState } from 'react';

function ProductImages({ imgUrls, name }) {
  const [ selectedIndex, setSelectedIndex ] = useState(-1);

  const handleClick = (index) => {
    if (selectedIndex === index) {
      setSelectedIndex(-1);
    } else {
      setSelectedIndex(index);
    }
  };

  const renderedThumbnails = imgUrls.map((url, index) => {
    return(
      <img key={index} className='thumbnail' src={url} alt/>
    )
  })


  return(
    <div className="images">
      <div className="main-img-container">
        <img onClick={handleClick} className="main-img" src={imgUrl} alt={name} />
      </div>
      <div className="thumbnail-container">
        <img className="thumbnail" src={imgUrl} alt={name} />
        <img className="thumbnail" src={imgUrl} alt={name} />
        <img className="thumbnail" src={imgUrl} alt={name} />
        <img className="thumbnail" src={imgUrl} alt={name} />
      </div>
    </div>
  )
}

export default ProductImages;
