  }

  const handleAddToCartClick = (name, price, quantity, img) => {
    setProduct({...product, name: name, price: price, quantity: quantity, img: img });
    setCart(prevCart => [...prevCart, product]);
  }

  const value = {
    product,
    cart,
    handleAddQuantityClick,
    handleSubtractQuantityClick,
    handleAddToCartClick,
  }

  return(
    <ShoesContext.Provider value={value}>
      {children}
    </ShoesContext.Provider>
  )
}

export { Provider };
export default ShoesContext;
