import { createContext, useState } from 'react';
import Product from '../components/Product';

const ShoesContext = createContext();

function Provider({ children }) {
  const [ products, setProducts ] = useState([]);
  const [ quantities, setQuantities ] = useState({});
  const [ selectedProduct, setSelectedProduct ] = useState({
    name: '',
    price: '',
    quantity: 0,
    img: ''
  });
  const [ selectedQuantity, setSelectedQuantity ] = useState(1);
  const [ cart, setCart ] = useState([]);


  const handleAddToCartClick = (name, price, quantity, img) => {
    setProduct({...product, name: name, price: price, quantity: quantity, img: img });
    setCart(prevCart => [...prevCart, product]);
  }

  const value = {
    products, 

    selectedProduct,
    selectedQuantity,
    cart,
    handleAddToCartClick,
  }

  return(
    <ShoesContext.Provider value={value}>
      {children}
    </ShoesContext.Provider>
  )
}

export { Provider };
export default ShoesContext;
