import { createContext, useEffect, useState, useCallback } from 'react';
import Data from '../data/data.json';

const ShoesContext = createContext();

function Provider({ children }) {
  const [ products, setProducts ] = useState([]);
  const [ quantities, setQuantities ] = useState({});
  const [ cart, setCart ] = useState([]);
  const [ cartQuantities, setCartQuantities ] = useState({});

  const handleQuantityChange = (id, newQuantity) => {
    setQuantities(prev => ({
      ...prev,
      [id]: newQuantity
    }));
    console.log('quantities', quantities);
    setCartQuantities(prev => ({
      ...prev, 
      [id]: quantities
    }));
    console.log('cartQuantities', cartQuantities);
  }

  const handleAddProduct = (id, name, price, img) => {
    if (cartQuantities[id]) {
      setCartQuantities(prev => ({
        ...prev,
        [id]: cartQuantities[id] + quantities[id]
      }));
      console.log('cartQuantities at handleAddProduct if product already exists in cart', cartQuantities);
      initializeQuantities();
    } else {
      setCartQuantities(prev => ({
        ...prev,
        [id]: quantities[id]
      }));
      console.log('cartQuantities add handleAddProduct if product is not already in cart', cartQuantities);
      initializeQuantities();
    }

    setCart((prevCart) => {
      const existingProductIndex = prevCart.findIndex(product => {
        return product.id === id;
      });

      if (existingProductIndex !== -1) {
        const updatedCart = [...prevCart];
        updatedCart[existingProductIndex].cartQuantity += quantities[id] || 1;

        return updatedCart;
      } else {

        return [...prevCart, {id, name, price, img, quantity: quantities[id], cartQuantity: cartQuantities[id]}]
      }
    });
  }



  const initializeQuantities = useCallback(() => {
    const initialQuantities = {};
    for (const product of products) {
      initialQuantities[product.id] = 0;
    };
    setQuantities(initialQuantities);
  }, [products]);

  const value = {
    products, 
    quantities,
    cartQuantities,
    handleQuantityChange,
    cart,
    handleAddProduct,
    discount: 50
  }

  useEffect(() => {
    setProducts(Data);
  }, []);

  useEffect(() => {
    initializeQuantities();
  }, [initializeQuantities]);


  return(
    <ShoesContext.Provider value={value}>
      {children}
    </ShoesContext.Provider>
  )
}

export { Provider };
export default ShoesContext;
