function ProductImages({ imgUrl, name }) {
  // const imageName = name.toLowerCase().replace(/\s/g, '-') + '.jpg';
  const imagePath = `../images/products/${imgUrl}`;

  const imagePromise = import(imagePath);

  return(
    <div>
      <img src={require(imagePromise).default} alt={name} />
    </div>
  )
}

export default ProductImages;
