import { createContext, useEffect, useState } from 'react';
import Data from '../data/data.json';

const ShoesContext = createContext();

function Provider({ children }) {
  const [ products, setProducts ] = useState([]);
  const [ quantities, setQuantities ] = useState({id: '', quantity: 0});
  const [ selectedProduct, setSelectedProduct ] = useState({
    name: '',
    price: '',
    // quantity: 0,
    // img: ''
  });

  const [ selectedQuantity, setSelectedQuantity ] = useState(1);
  const [ cart, setCart ] = useState([]);

  const handleAddProduct = (name, price) => {
    console.log('this is name and price args passed into handleAddProduct', name, price);
    setSelectedProduct(prev => ({
        ...prev,
        name: name,
        price: price,
    }));
    setCart((prev) => [...prev, {...selectedProduct}]
    )

    console.log('this is cart after calling setCart', cart);
    // console.log(selectedProduct);
    // console.log(cart);
  }

  const value = {
    products, 
    selectedProduct,
    quantities,
    selectedQuantity,
    cart,
    handleAddProduct
  }

  useEffect(() => {
    setProducts(Data);
  }, []);

  return(
    <ShoesContext.Provider value={value}>
      {children}
    </ShoesContext.Provider>
  )
}

export { Provider };
export default ShoesContext;
