import SummerSneakers from '../images/products/summer-limited-edition-sneakers.jpg';

function ProductImages({ name }) {
  const imageName = name.toLowerCase().replace(/\s/g, '-') + '.jpg';
  const imagePath = `../images/products/${imageName}`;

  const imagePromise = import(imagePath);

  return(
    <div>
      <img src={imagePromise.default} alt={name} />
    </div>
  )
}

export default ProductImages;
